export default (_) => {
	function html(glob) {
		return _.gulp
			.src(glob)
			.pipe(
				_.plumber(function (err) {
					console.error(err.message.toString());
					this.emit("end");
				}),
			)
			.pipe(
				_.pug({
					pretty: "\t",
				}),
			)
			.pipe(_.gulp.dest("dist"));
	}

	function css() {
		return _.gulp
			.src("src/styles/**.scss", "!src/styles/_*.scss")
